import React from "react";
import { connect } from "react-redux";
import { signIn, signOut } from "../actions/index";

class GoogleAuth extends React.Component {
  componentDidMount() {
    window.gapi.load("client:auth2", () => {
      window.gapi.client
        .init({
          client_id:
            "458587909442-6f4lt2mubf4s307mv5i5anbpuqofsg3h.apps.googleusercontent.com",
          scope: "email",
          plugin_name: "Streamy",
        })
        .then(() => {
          this.auth = window.gapi.auth2.getAuthInstance();
          this.userId = this.auth.currentUser;
          this.onAuthChange(this.auth.isSignedIn.get());
          this.auth.isSignedIn.listen(this.onAuthChange);
        });
    });
  }
  onAuthChange = (isSignedIn) => {
    //choose witch action will be sent to the reducer
    if (isSignedIn) {
      //send this this.auth.currentUser.get().getId() to the reducer
      this.props.signIn(this.userId.get().getId());
    } else {
      this.props.signOut();
    }
  };

  onSignInClick = () => {
    this.auth.signIn();
  };

  onSignOutClick = () => {
    this.auth.signOut();
  };
  renderAuthButton() {
    if (this.props.isSignedIn === null) {
      return <div>I dont know if we are signed in</div>;
    } else if (this.props.isSignedIn === true) {
      return (
        <button className="ui red google button" onClick={this.onSignOutClick}>
          <i className="google icon" />
          Sign Out
        </button>
      );
    } else {
      return (
        <button className="ui red google button" onClick={this.onSignInClick}>
          <i className="google icon" />
          Sign in with google
        </button>
      );
    }
  }

  render() {
    return <div>{this.renderAuthButton()}</div>;
  }
}
const mapStateToProps = (state) => {
  return {
    isSignedIn: state.auth.isSignedIn,
    userId: state.userId.get().getId(),
  };
};

export default connect(mapStateToProps, { signIn, signOut })(GoogleAuth);
