import React, { useRef } from "react";

class GoogleAuth extends React.Component {
  state = { isSignedIn: null };

  componentDidMount() {
    window.google.accounts.id.initialize({
      client_id:
        "458587909442-kgb3t8e290oic5pn07elr8vb1efilico.apps.googleusercontent.com",
      scope: "email",
      callback: onGoogleSignIn,
    });

    // this.auth = window.gapi.auth2.getAuthInstance();
    // this.setState({ isSignedIn: this.auth.isSignedIn.get() });
    // this.auth.isSignedIn.listen(this.onAuthChange);

    const googleSignInButton = useRef(null);

    window.google.accounts.id.renderButton(
      googleSignInButton.current,
      { theme: "outline", size: "large", text, width: "250" } // customization attributes
    );
  }

  // const onGoogleSignIn = () => {
  //   window.google.accounts.id.renderButton(
  //     googleSignInButton.current,
  //     { theme: "outline", size: "large", text: "hellow", width: "250" } // customization attributes
  //   );
  // };

  onAuthChange = () => {
    this.setState({ isSignedIn: this.auth.isSignedIn.get() });
  };
  renderAuthButton() {
    if (this.state.isSignedIn === null) {
      return <div>I dont know if we are signed in</div>;
    } else if (this.state.isSignedIn === true) {
      return <div>I am singned in</div>;
    } else {
      return <div>i am not signed in</div>;
    }
  }
  render() {
    return <div>{this.renderAuthButton()}</div>;
  }
}

export default GoogleAuth;
