import React, { useEffect } from "react";
import { gapi } from "gapi-script";

GoogleAuth = () => {
  useEffect(() => {
    function start() {
      gapi.client.init({
        clientId:
          "458587909442-kgb3t8e290oic5pn07elr8vb1efilico.apps.googleusercontent.com",
        scope: "email",
      });
    }

    gapi.load("client:auth2", start);
  }, []);
};

export default GoogleAuth;
